namespace Olif;

require_once CORE_ROOT . CONTROLLERS . DIRECTORY_SEPARATOR . "ControllerApp.php";

class ControllerRequest extends ControllerApp
{

    /**
     * crackers_mensaje
     * Mensaje a mostrar a los que traten de pasar las medidas de seguridad.
     *
     * @var string
     */
    private $crackers_mensaje = '<img src="data:image/jpg;base64,"/>';

    /**
     * get
     *
     * @param
     *
     */
    protected function get($var, $check_xss = true, $encode = "UTF8_D")
    {
        if ($check_xss === true) {
            if (! $this->checkXSS($var))
                return false;
        }
        if (! is_array($var) && ! is_bool($var)) {
            if ($encode == "UTF8_D") {
                $var = utf8_decode($var);
            } elseif ($encode == "UTF8_E") {
                $var = utf8_encode($var);
            }
            $var = trim($var);
        }
        return $var;
    }

    /**
     * checkXSS
     *
     * @param
     *
     */
    public function checkXSS($var)
    {
        if (is_array($var)) {
            if ($this->detectXSSInArray($var)) {
                return $this->alertXSS($var);
            }
        }
        if ($this->detectXSS($var)) {
            return $this->alertXSS($var);
        }

        return true;
    }

    /**
     * getVar
     * Recoge la variable indicada en $name por POST o GET y verifica comprobaciones m√≠nimas de seguridad
     *
     * @param string $name
     *            nombre de la variable a recoger
     */
    public function getVar($name, $stripHMTLtags = true, $check_xss = true, $encode = "NONE")
    {
        if (isset($_POST[$name])) {
            $var = $_POST[$name];
        } elseif (isset($_GET[$name])) {
            $var = $_GET[$name];
        } else {
            $var = false;
        }
        if ($var !== false) {
            $var = $this->get($var, $check_xss, $encode);
            if (! is_array($var) && ! is_bool($var)) {
                if ($stripHMTLtags === true)
                    $var = strip_tags($var);
            }
        }
        return $var;
    }

    /**
     * getAPIVars
     */
    public function getAllGetVar()
    {
        return $_GET;
    }

    /**
     * getAllPostVar
     */
    public function getAllPostVar()
    {
        return array_merge($_POST, $_FILES);
    }

    /**
     * A utility function to manage nested array structures, checking
     * each value for possible XSS.
     * Function returns boolean if XSS is
     * found.
     *
     * @param array $array
     *            An array of data to check, this can be nested arrays.
     * @return boolean True if XSS is detected, false otherwise
     */
    public static function detectXSSInArray($array)
    {
        foreach ($array as $value) {
            if (is_array($value)) {
                return self::detectXSSInArray($value);
            } elseif (self::detectXSS($value) === true) {
                    return true;
            }
        }
        return false;
    }

    /**
     * Given a string, this function will determine if it potentially an
     * XSS attack and return boolean.
     *
     * @param string $string
     *            The string to run XSS detection logic on
     * @return boolean True if the given `$string` contains XSS, false otherwise.
     */
    public static function detectXSS($string)
    {
        $contains_xss = false;
        // Skip any null or non string values
        if (is_null($string) || ! is_string($string)) {
            return $contains_xss;
        }
        // Keep a copy of the original string before cleaning up
        $orig = $string;
        // URL decode
        $string = urldecode($string);
        // Convert Hexadecimals
        if (version_compare(PHP_VERSION, '5.3.0', '<')) {
            $string = preg_replace_callback('!(&#|\\\)[xX]([0-9a-fA-F]+);?!', 'auxDetectXSS', $string);
        } else {
            $string = preg_replace_callback('!(&#|\\\)[xX]([0-9a-fA-F]+);?!', function ($m)
            {
                return chr(hexdec($m[2]));
            }, $string);
        }
        // Clean up entities
        $string = preg_replace('!(&#0+[0-9]+)!', '$1;', $string);
        // Decode entities
        $string = html_entity_decode($string, ENT_NOQUOTES, 'UTF-8');
        // Strip whitespace characters
        $string = preg_replace('!\s!', '', $string);
        // Set the patterns we'll test against
        $patterns = array(

            // Match any attribute starting with "on" or xmlns
            '#(<[^>]+[\x00-\x20\"\'\/])(on|xmlns)[^>]*>?#iUu',

            // Match javascript:, livescript:, vbscript: and mocha: protocols
            '!((java|live|vb)script|mocha):(\w)*!iUu',
            '#-moz-binding[\x00-\x20]*:#u',

            // Match style attributes
            '#(<[^>]+[\x00-\x20\"\'\/])style=[^>]*>?#iUu',

            // Match unneeded tags
            '#</*(applet|meta|xml|blink|link|style|script|embed|object|iframe|frame|frameset|ilayer|layer|bgsound|title|base)[^>]*>?#i'
        );
        foreach ($patterns as $pattern) {
            // Test both the original string and clean string
            if (preg_match($pattern, $string) || preg_match($pattern, $orig)) {
                $contains_xss = true;
            }
            if ($contains_xss === true)
                return true;
        }
        return false;
    }

    public function auxDetectXSS($m)
    {
        return chr(hexdec($m[2]));
    }

    /**
     * removeXSS
     *
     * Elimina los posibles ataques XSS
     */
    public function alertXSS($val)
    {
        echo $this->crackers_mensaje;
        $result = $this->sendError("[ERROR " . get_class($this) . "::" . __FUNCTION__ . "::" . __LINE__ . "] Se detect√≥ c√≥digo malicioso con el contenido: <pre>".$val."</pre>");
        return false;
    }
}
